    const char = afterShinonome[i];

    if \(char === '['\) {
        nestLevel++;
        inArray = true;
    } else if \(char === ']'\) {
        nestLevel--;
        if \(inArray && nestLevel === 0\) {
            // Found the closing bracket
            // Now look for the comma after it
            if \(afterShinonome[i + 1] === ','\) {
                endIdx = i + 2;
                break;
            }
        }
    }
}

